    public static class DatabaseHelper extends SQLiteOpenHelper {


        DatabaseHelper(Context context) {
            super(context, DATABASE_NAME, null, DATABASE_VERSION);
        }

        @Override
        public void onCreate(SQLiteDatabase db) {
            db.execSQL(CHARACTERS_DATABASE_CREATE);
            db.execSQL(ABILITIES_DATABASE_CREATE);
            db.execSQL(SPELLS_DATABASE_CREATE);
            db.execSQL(MELEE_DATABASE_CREATE);
            db.execSQL(ARMOR_DATABASE_CREATE);
            db.execSQL(CAREERS_DATABASE_CREATE);
            db.execSQL(ARCHETYPE_DATABASE_CREATE);
            db.execSQL(RANGE_DATABASE_CREATE);
        }

        @Override
        public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {
            Log.w(TAG, "Upgrading database from version " + oldVersion + " to " + newVersion + ", which will destroy all old data");
            db.execSQL("DROP TABLE IF EXISTS " + SQLITE_CHARACTER_TABLE);
            db.execSQL("DROP TABLE IF EXISTS " + SQLITE_ABILITIES_TABLE);
            db.execSQL("DROP TABLE IF EXISTS " + SQLITE_SPELLS_TABLE);
            db.execSQL("DROP TABLE IF EXISTS " + SQLITE_MELEE_TABLE);
            db.execSQL("DROP TABLE IF EXISTS " + SQLITE_ARMOR_TABLE);
            db.execSQL("DROP TABLE IF EXISTS " + SQLITE_RANGE_TABLE);
            db.execSQL("DROP TABLE IF EXISTS " + SQLITE_CAREER_TABLE);
            db.execSQL("DROP TABLE IF EXISTS " + SQLITE_ARCHETYPE_TABLE);
            onCreate(db);
        }
    }

    public Database(Context context) {
        this.mCtx = context;
    }

    public Database open() throws SQLException {
        DatabaseHelper mDbHelper = new DatabaseHelper(mCtx);
        mDb = mDbHelper.getReadableDatabase();
        return this;
    }



