    public Cursor fetchAbilitiesByName(String inputText) throws SQLException {
        Log.w(TAG, inputText);
        Cursor cursor;
        if (inputText == null || inputText.length() == 0) {
            cursor = mDb.query(SQLITE_ABILITIES_TABLE, new String[] {KEY_ROWID, KEY_ABILITIES_NAME, KEY_ABILITIES_PREREQUISITE, KEY_ABILITIES_DESCRIPTION}, null, null, null, null, null);
        } else {
            cursor = mDb.query(true, SQLITE_ABILITIES_TABLE, new String[] {KEY_ROWID, KEY_ABILITIES_NAME, KEY_ABILITIES_PREREQUISITE, KEY_ABILITIES_DESCRIPTION}, KEY_ABILITIES_NAME + " like '%" + inputText + "%'", null,
                    null, null, null, null);
        }
        if (cursor != null)
            cursor.moveToFirst();
        return cursor;
    }

